import React, { useState } from 'react';
import {
  Button,
  InputContainer,
  PageWrapper,
  TodoCard,
  TodoContainer,
  TodoHeader,
  TodoListContainer,
} from './components/styles';
import nextId from 'react-id-generator';
import { useDispatch, useSelector } from 'react-redux';
import { __addToDo, __deleteTodo } from './redux/modules/todosSlice';

function App() {
  const id = nextId();
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.todos.list);
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');

  const onAddTodo = (event) => {
    /**
     * ÏãúÌóò Î¨∏Ï†ú 1.
     * Ïù¥Í≥≥ÏóêÏÑú Ï∂îÍ∞ÄÌïòÍ∏∞ Í∏∞Îä•ÏùÑ Íµ¨ÌòÑÌï¥Ï£ºÏÑ∏Ïöî.
     */
    event.preventDefault();
    const addTodo = {
      id,
      title,
      body,
    };
    dispatch(__addToDo(addTodo));
  };

  const onDeleteTodo = (id) => {
    /**
     * ÏãúÌóò Î¨∏Ï†ú 2.
     * Ïù¥Í≥≥ÏóêÏÑú ÏÇ≠Ï†úÌïòÍ∏∞ Í∏∞Îä•ÏùÑ Íµ¨ÌòÑÌï¥Ï£ºÏÑ∏Ïöî.
     */
    dispatch(__deleteTodo(id));
  };

  const resetInputs = () => {
    /**
     * ÏûÖÎ†• Í∞íÏùÑ Ï¥àÍ∏∞ÌôîÌïòÍ≥† Ïã∂Îã§Î©¥ ÏÇ¨Ïö©ÌïòÏÑ∏Ïöî.
     */
    setTitle('');
    setBody('');
  };
  const onChangeTitle = (e) => setTitle(e.target.value);
  const onChangeBody = (e) => setBody(e.target.value);
  return (
    <PageWrapper>
      <TodoContainer>
        <TodoHeader>üê¢ SLOW TODO LIST üê¢</TodoHeader>
        <InputContainer>
          <span>Ï†úÎ™©: </span>
          <input
            value={title}
            placeholder="Ìï† Ïùº Ï†úÎ™©"
            onChange={onChangeTitle}
          />
          <span>ÎÇ¥Ïö©: </span>
          <input
            value={body}
            placeholder="Ìï† Ïùº ÎÇ¥Ïö©"
            onChange={onChangeBody}
          />

          <Button onClick={onAddTodo}>+ Ï∂îÍ∞ÄÌïòÍ∏∞</Button>
        </InputContainer>
        <TodoListContainer>
          {todos.map((todo) => (
            <TodoCard key={todo.id}>
              <span>Ï†úÎ™©: {todo.title}</span>
              <span>Ìï† Ïùº: {todo.body}</span>
              <Button onClick={onDeleteTodo(todo.id)}>ÏÇ≠Ï†úÌïòÍ∏∞</Button>
            </TodoCard>
          ))}
        </TodoListContainer>
      </TodoContainer>
    </PageWrapper>
  );
}

export default App;
